     var DOMabsOffset = function(target){
        var top = target.offsetTop;
        var left = target.offsetLeft;
         
        while(target = target.offsetParent) {
          top += target.offsetTop;
          left += target.offsetLeft;
        }
         
        return {offsetLeft:left, offsetTop:top};
      };

